import React from "react";
import './who-to-follow.css';

const WhoToFollowListItem = ({
                                 who = {
                                     avatarIcon: '../../../images/nasa.jpeg',
                                     userName: 'NASA',
                                     handle: 'NASA',
                                 }

                             }) => {
    return (
        <li className = "list-group-item">
            <div className = "row">
                <div className="col-2">
                    <img src = {who.avatarIcon} width = "48" className="rounded-circle float-start" />
                </div>
                <div className="col-8">
                    <div className = "fw-bold">{who.userName}
                        <span className = "fa-stack" style={{"font-size": "0.5em"}}>
                            <i className = "fas fa-circle fa-stack-2x"></i>
                            <i className = "fas fa-check fa-stack-1x fa-inverse"></i>
                        </span>
                    </div>
                    <div>@{who.handle}</div>
                </div>
                <div className="col-2">
                    <button className="bg-color-blue rounded-pill float-end btn btn-primary mt-1">Follow</button>
                </div>
            </div>
        </li>
            );
        };

export default WhoToFollowListItem;
